import {useState} from 'react';
import Exercises from './Exercises';
import Stopwatch from './Stopwatch';
import Timer from './Timer';
import photo1 from './jumpingJacks.gif'
import photo2 from './jumpSquat.gif'
import {useEffect} from 'react'

const ExerciseDisplay = ({time, focus, level}) => {
    const [exercises, setExercises] = useState([
        {name: "Jumping Jacks", reprange: 15, photo: photo1, id:1, focus: "cardio", level: 1 }, 
        {name: "Jump Squat", reprange: 10, photo: photo2, id: 2, focus: "legs", level: 3 },
        {name: "Pushups", reprange: 10, id: 3, focus: "upper", level: 2}, 
        {name: "Explosive Situps", reprange: 20, id: 4, focus: "abs", level: 2},
        {name: "Burpees", reprange: 15, photo: photo1, id:5, focus: "cardio", level: 3 }, 
        {name: "Lunges", reprange: 10, photo: photo2, id: 6, focus: "legs", level: 1},
        {name: "Commandos", reprange: 10, id: 7, focus: "upper", level: 3}, 
        {name: "Plank", reprange: 20, id: 8, focus: "abs", level: 1},
        {name: "High Knees", reprange: 15, photo: photo1, id:9, focus: "cardio" , level:3}, 
        {name: "Jump Lunges", reprange: 10, photo: photo2, id: 10, focus: "legs", level: 3},
        {name: "Downward Dog to Pushups", reprange: 10, id: 11, focus: "upper", level: 3}, 
        {name: "Leg Raises", reprange: 20, id: 12, focus: "abs", level: 1},
        {name: "Skaters", reprange: 15, photo: photo1, id:13, focus: "cardio" , level:1}, 
        {name: "Step Ups", reprange: 10, photo: photo2, id: 14, focus: "legs", level:1},
        {name: "PullUps", reprange: 10, id: 15, focus: "upper", level:3}, 
        {name: "Crunches", reprange: 20, id: 16, focus: "abs", level:1},
        {name: "Squats", reprange: 10, photo: photo2, id: 17, focus: "legs", level:1},
        {name: "Lateral Leg Raises", reprange: 10, photo: photo2, id: 18, focus: "legs", level:1},
        {name: "Glute Bridges", reprange: 10, photo: photo2, id: 19, focus: "legs", level:1},
        {name: "Single Leg Glute Bridges", reprange: 10, photo: photo2, id: 20, focus: "legs", level:2},
        {name: "Mountain Climbers", reprange: 10, photo: photo2, id: 21, focus: "cardio", level:2},
        {name: "Donkey Kicks", reprange: 10, photo: photo2, id: 22, focus: "cardio", level:1},
    ])

    const exerciseBank = [
        {name: "Jumping Jacks", reprange: 15, photo: photo1, id:1, focus: "cardio" }, 
        {name: "Jump Squat", reprange: 10, photo: photo2, id: 2, focus: "legs"},
        {name: "Pushups", reprange: 10, id: 3, focus: "upper"}, 
        {name: "Explosive Situps", reprange: 20, id: 4, focus: "abs"},
        {name: "Burpees", reprange: 15, photo: photo1, id:5, focus: "cardio" }, 
        {name: "Lunges", reprange: 10, photo: photo2, id: 6, focus: "legs"},
        {name: "Commandos", reprange: 10, id: 7, focus: "upper"}, 
        {name: "Plank", reprange: 20, id: 8, focus: "abs"},
        {name: "High Knees", reprange: 15, photo: photo1, id:9, focus: "cardio" }, 
        {name: "Jump Lunges", reprange: 10, photo: photo2, id: 10, focus: "legs"},
        {name: "Downward Dog to Pushups", reprange: 10, id: 11, focus: "upper"}, 
        {name: "Leg Raises", reprange: 20, id: 12, focus: "abs"},
        {name: "Skaters", reprange: 15, photo: photo1, id:13, focus: "cardio" }, 
        {name: "Step Ups", reprange: 10, photo: photo2, id: 14, focus: "legs"},
        {name: "PullUps", reprange: 10, id: 15, focus: "upper"}, 
        {name: "Crunches", reprange: 20, id: 16, focus: "abs"},
        {name: "Squats", reprange: 10, photo: photo2, id: 17, focus: "legs"},
        {name: "Lateral Leg Raises", reprange: 10, photo: photo2, id: 18, focus: "legs"},
        {name: "Glute Bridges", reprange: 10, photo: photo2, id: 19, focus: "legs"},
        {name: "Single Leg Glute Bridges", reprange: 10, photo: photo2, id: 20, focus: "legs"},
        {name: "Mountain Climbers", reprange: 10, photo: photo2, id: 21, focus: "cardio"},
        {name: "Donkey Kicks", reprange: 10, photo: photo2, id: 22, focus: "cardio"},
    ]

    //const level = 1
    //const target = 0


    const handleDelete = (id) => {
        console.log(id)
        if (id != null){
            const newExercises = exercises.filter(exercise => exercise.id != id);
            setExercises(newExercises);
        }
    } 

    const[startTime, setStartTime] = useState([
        {time: 45, id: 1},
        {time: 45, id: 2},
        {time: 45, id: 3}
    ])

    const [time1, setTime1] = useState(time)

    /*useEffect(() => {
        if (time1 === 5) {
            let newExercises = []
            for (let i = 0; i<4; i++){
                newExercises.push(exerciseBank[Math.floor(Math.random()*exerciseBank.length)])
            }
            setExercises(newExercises);
        }
        if (time1 === 15){ 
            let newExercises = []
            for (let i = 0; i<15; i++){
                const random = Math.floor(Math.random()*exerciseBank.length)
                console.log(random + " " +   exercises.length)
                console.log(exerciseBank)
                newExercises.push(exerciseBank[random])
            }
            setExercises(newExercises);
        }
        if (time1 === 0){
            setExercises([{}])
        }
    }, [time1])*/

    const changeDisplay = (time, level, focus) => {

        if (focus != 'fullBody'){
            var newExerciseBank = exerciseBank.filter(exercise => exercise.focus === focus)
            newExerciseBank = newExerciseBank.filter(exercise => exercise.level <= level)
        }
        else{
            var newExerciseBank = exerciseBank
        }

        console.log("whats happening")
        let newExercises = []
        let added = new Map()
        for (let i = 0; i<time; i++){
            let random = Math.floor(Math.random()*newExerciseBank.length)
            if (newExerciseBank.length > time) {
                while (added[random] === 1){
                random =Math.floor(Math.random()*newExerciseBank.length)
                }
            }
            newExercises.push(newExerciseBank[random])
            added[random] = 1
            }
        console.log(newExercises)
        setExercises(newExercises);
        console.log(exercises)

    }

    return(
        <div>
            <button onClick={()=> changeDisplay(time, level, focus)} style={{marginLeft: '45%'}}> Let's Go!</button>
            <Stopwatch startTime = {startTime} id={exercises[0].id} handleDelete={handleDelete} />
            <h1> Time: {time} Focus: {focus} Level: {level}</h1>
            <Exercises Exercises = {exercises} handleDelete={handleDelete}/>
        </div>
    );
}
export default ExerciseDisplay;
