import TimerDisplay from './TimerDisplay'
import TimerButton from './TimerButton'
import {useState} from 'react';
import {useEffect} from 'react';

const Stopwatch = ({startTime, id, handleDelete}) => {
    const [time, setTime] = useState({ms: 0, s: 5, m:0, h:0});
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(0);
    //Status 0 : waiting
    //Status 1: running
    //Status 2: stopped

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }

    var updatedMs = time.ms
    var updatedS = time.s
    var updatedM = time.m
    var updatedH = time.h


    //Checks if timer has reached 0 -> if so, resets timer and deletes most recent exercise
    /*useEffect(() => {
        if (updatedMs === 0 && updatedS === 0 && updatedM === 0 & updatedH === 0){
            reset();
            if (id != null){
            handleDelete(id)}
        }
    }
    )*/

    //for continuously running timer: 
    useEffect(() => {
        if (updatedMs === 0 && updatedS === 0 && updatedM === 0 & updatedH === 0){
            updatedS = 45;
            start();
            if (id != null){
            handleDelete(id)}
        }
    }
    )

    //Decrements every ms
    const run = () => {
        if (updatedM === 0){
            if (updatedH >0){
                updatedH--;
                updatedM = 59;
            }
        }
        if (updatedS === 0){
            if (updatedM > 0){
                updatedM --;
                updatedS = 59;
            }
        }
        if (updatedMs === 0){
            if (updatedS > 0){
                updatedS--;
                updatedMs = 99;
            }
        }
        updatedMs --;
        //console.log("timeron")
        return setTime({ms: updatedMs, s: updatedS, m:updatedM, h:updatedH})
    }   

    const stop = () => {
        clearInterval(interv);
        setStatus(2);
        console.log("Timer Stopped")
    }


    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({ms: 0, s: 5, m:0, h:0})
        console.log(time, interv, status);
    }

    const resume = () => start();


    return (
        <div className = "timer-holder">
            <h1> Timer</h1>         
            <TimerDisplay time={time}></TimerDisplay>
            <TimerButton start={start} status={status} stop={stop} reset={reset} resume={resume}> </TimerButton>
        </div> 
    )
}

export default Stopwatch
