import {useState} from 'react';
import Exercises from './Exercises';
import Stopwatch from './Stopwatch';
import Timer from './Timer';
import photo1 from './jumpingJacks.gif'
import photo2 from './jumpSquat.gif'
import {useEffect} from 'react'
import React from 'react'

const ExerciseDisplay = ({num, focus, level, time}) => {
    const myRef = React.createRef()
    const [exercises, setExercises] = useState(null)
    const [exerciseBank, setExerciseBank] = useState(null)

    //called when the timer reaches 0 -> deletes the first exercise once completed, changes exercise state 
    const handleDelete = (id) => {
        console.log(id)
        if (id != null){
            const newExercises = exercises.filter(exercise => exercise.id != id);
            setExercises(newExercises);
        }
        else{

        }
    } 

    const[startTime, setStartTime] = useState([
        {time: 45, id: 1},
        {time: 45, id: 2},
        {time: 45, id: 3}
    ])

    //const [time1, setTime1] = useState(time)

   //fetch data from backend
   useEffect(()=> {
       fetch('http://127.0.0.1:8000/api/workout/')
        .then(res => {
           return res.json();
        })
        .then(data => {
            console.log(data)
            setExerciseBank(data)
        })
   }, []);

    //onload, exercises are not displayed
    const [showResults, setShowResults] = useState(false)

    //once submit button is changed, exercises are loaded up based on given values
    const changeDisplay = (num, level, focus) => {
        console.log("Changing exercise list to: " + num + level + focus)
        //first button click reveals this group of components 
        setShowResults(true)

        //scroll to exercises when button is clicked
        window.scrollTo(0, myRef.current.offsetTop);

        var focusID = null
        if (focus === 'upper'){
            focusID = 1
        }
        if (focus === 'legs'){
            focusID = 2
        }
        if (focus === 'cardio'){
            focusID = 3
        }
        if (focus === 'abs'){
            focusID = 4
        }

        console.log(exercises)
        var newExerciseBank = exerciseBank
        if (focus != 'fullBody'){
            newExerciseBank = exerciseBank.filter(exercise => exercise.focus === focusID)
        }
        console.log("newBank1: ", newExerciseBank)
        if (focus!= 'abs'){
            newExerciseBank = newExerciseBank.filter(exercise => exercise.level <= level)
        }
        console.log("newBank2: ", newExerciseBank)

        let newExercises = []
        let added = new Map()
        for (let i = 0; i<num; i++){
            let random = Math.floor(Math.random()*newExerciseBank.length)
            if (newExerciseBank.length > num) {
                while (added[random] === 1){
                random =Math.floor(Math.random()*newExerciseBank.length)
                }
            }
            newExercises.push(newExerciseBank[random])
            added[random] = 1
            }
        console.log(newExercises)
        setExercises(newExercises);

    }

    if (exercises != null){
        if (exercises.length > 0){
            var id = exercises[0].id;
        }
        else{
            var id = null
        }
    }

    return(
        <div>
            <button className="submitButton" onClick={()=> changeDisplay(num, level, focus)} style={{marginLeft: '45%'}}> Let's Go!</button>
            <div ref={myRef}> {showResults ? <Stopwatch startTime = {startTime} id={id} handleDelete={handleDelete} /> : null} </div>
            <div> {showResults ? <h1> Num Exercises: {num} Focus: {focus} Level: {level}</h1> : null}</div>
            {exercises && <div> {showResults? <Exercises Exercises = {exercises} handleDelete={handleDelete}/> : null} </div>}
        </div>
    );
}
export default ExerciseDisplay;
