import TimerDisplay from './TimerDisplay'
import TimerButton from './TimerButton'
import {useState} from 'react';


const Stopwatch = ({startTime}) => {
    const [time, setTime] = useState({ms: 0, s: {startTime}, m:0, h:0});
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(0);
    //Status 0 : waiting
    //Status 1: running
    //Status 2: stopped

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }

    var updatedMs = time.ms
    var updatedS = time.s
    var updatedM = time.m
    var updatedH = time.h

    const run = () => {
        if (updatedM === 0){
            if (updatedH >0){
                updatedH--;
                updatedM = 59;
            }
        }
        if (updatedS === 0){
            if (updatedM > 0){
                updatedM --;
                updatedS = 59;
            }
        }
        if (updatedMs === 0){
            if (updatedS > 0){
                updatedS--;
                updatedMs = 99;
            }

        }
        if (updatedMs === 0 && updatedS === 0 && updatedM === 0 && updatedH=== 0){
            clearInterval(interv);
            setStatus(0);
            setTime({ms: 0, s: 10, m:0, h:0});
            return 
        }
        updatedMs --;
        return setTime({ms: updatedMs, s: updatedS, m:updatedM, h:updatedH})

    }   

    const stop = () => {
        clearInterval(interv);
        setStatus(2);
    }

    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({ms: 0, s: 10, m:0, h:0})
    }

    const resume = () => start();


    return (
        <div className = "timer-holder">
            <h1> Timer</h1>         
            <TimerDisplay time={time}></TimerDisplay>
            <TimerButton start={start} status={status} stop={stop} reset={reset} resume={resume}> </TimerButton>
        </div> 
    )
}

export default Stopwatch
